import React, { useState, useEffect } from 'react';

const App = () => {
  const [command, setCommand] = useState('');
  const [responses, setResponses] = useState([]);
  const [currentWord, setCurrentWord] = useState('Innovation');
  const words = ['Innovation', 'Creativity', 'AI Excellence', 'Empowerment'];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentWord(prevWord => {
        const currentIndex = words.indexOf(prevWord);
        const nextIndex = (currentIndex + 1) % words.length;
        return words[nextIndex];
      });
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:8000/process-command', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ command }),
      });
      const data = await res.json();
      if (res.ok) {
        setResponses(data.messages || [{type: 'message', content: 'Command executed successfully.'}]);
      } else {
        throw new Error(data.message || 'Failed to execute command.');
      }
    } catch (error) {
      setResponses([{type: 'error', content: `Error: Unable to process the command. ${error.message}`}]);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-800 to-purple-500 flex flex-col items-center">
      <header className="w-full px-6 py-4 flex items-center fixed top-0 bg-gradient-to-r from-purple-900 to-purple-700 shadow-lg">
        <div className="container mx-auto flex items-center">
          <div className="flex items-center gap-4">
            <img src="/paal-logo.png" alt="Paal AI Logo" className="h-12 w-auto" />
            <h1 className="text-3xl font-bold text-white tracking-wide">PAAL AI</h1>
          </div>
          <div className="ml-8 text-cyan-300 text-xl font-light animate-pulse">
            {currentWord}
          </div>
        </div>
      </header>

      <main className="container mx-auto mt-24 px-4 flex-grow flex items-center justify-center">
        <div className="w-full max-w-3xl bg-white/10 backdrop-blur-lg rounded-xl p-6 shadow-xl">
          <div className="h-80 overflow-y-auto mb-6">
            {responses.map((msg, index) => (
              <div key={index} className="bg-white/5 border-l-4 border-cyan-400 rounded p-4 mb-4">
                <p className="text-white">{msg.content}</p>
              </div>
            ))}
          </div>

          <form onSubmit={handleSubmit} className="flex gap-4">
            <textarea
              className="flex-grow p-4 rounded-lg bg-white/5 border border-cyan-400/50 text-white placeholder-white/50 focus:outline-none focus:border-cyan-400 resize-none"
              placeholder="Enter your command..."
              value={command}
              onChange={(e) => setCommand(e.target.value)}
              rows="3"
            />
            <button 
              type="submit" 
              className="px-6 bg-cyan-400 hover:bg-cyan-300 text-purple-900 font-semibold rounded-lg transition-colors"
            >
              Send
            </button>
          </form>
        </div>
      </main>

      <footer className="w-full py-4 text-center text-white/60 text-sm">
        Powered by <span className="text-cyan-400">PAAL AI</span>
      </footer>
    </div>
  );
};

export default App;
