import React, { useState, useEffect } from 'react';
import './App.css'; // Ensure the CSS file is imported to apply styles.

const App = () => {
  const [command, setCommand] = useState('');
  const [responses, setResponses] = useState([]);
  const [currentWord, setCurrentWord] = useState('Innovation');

  const words = ['Innovation', 'Creativity', 'AI Excellence', 'Empowerment'];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentWord(prevWord => {
        const currentIndex = words.indexOf(prevWord);
        const nextIndex = (currentIndex + 1) % words.length;
        return words[nextIndex];
      });
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('http://localhost:8000/process-command', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ command }),
      });

      const data = await res.json();
      if (res.ok) {
        setResponses(data.messages || [{type: 'message', content: 'Command executed successfully.'}]);
      } else {
        throw new Error(data.message || 'Failed to execute command.');
      }
    } catch (error) {
      setResponses([{type: 'error', content: `Error: Unable to process the command. ${error.message}`}]);
    }
  };

  return (
    <div className="app">
      <header className="app-header">
        <img src="/paal-logo.png" alt="Paal AI Logo" className="app-logo" />
        <h1>Paal AI</h1>
      </header>

      <div className="chat-container">
        <div className="messages">
          {responses.map((msg, index) => (
            <p key={index} className="message">{msg.content}</p>
          ))}
        </div>
        <form onSubmit={handleSubmit} className="message-form">
          <textarea
            className="message-input"
            placeholder="Enter your command..."
            value={command}
            onChange={(e) => setCommand(e.target.value)}
          />
          <button type="submit" className="send-button">Send</button>
        </form>
      </div>

      <footer className="app-footer">
        <p>Powered by <span>Paal AI</span></p>
      </footer>
    </div>
  );
};

export default App;
