import React, { useState } from 'react';

const App = () => {
  const [command, setCommand] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/process-command', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ command }),
      });

      const data = await res.json();
      setResponse(data.message || 'Command executed successfully.');
    } catch (error) {
      setResponse('Error: Unable to execute the command.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 via-pink-500 to-purple-700 text-white flex flex-col items-center justify-center">
      {/* Header Section */}
      <header className="absolute top-4 left-4 flex items-center">
        {/* Add the Paal AI Logo */}
        <img src="/paal-logo.png" alt="Paal AI Logo" className="h-12 mr-3" />
        <div>
          <h1 className="text-3xl font-bold">Paal AI</h1>
          <p className="text-lg">AI-Powered-PC</p>
        </div>
      </header>

      <div className="flex w-full max-w-5xl mt-20">
        {/* Left Output Box */}
        <div className="flex-1 bg-white text-black shadow-lg rounded-lg p-4 mr-4">
          <h2 className="text-xl font-bold mb-2">Response:</h2>
          <div className="overflow-y-auto h-64 p-2 border border-gray-300 rounded">
            {response ? <p>{response}</p> : <p>No response yet...</p>}
          </div>
        </div>

        {/* Query Input Box */}
        <div className="flex-1 bg-white text-black shadow-lg rounded-lg p-4">
          <h2 className="text-xl font-bold mb-2">Enter Your Query:</h2>
          <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
            <textarea
              className="border border-gray-300 rounded-lg p-4 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter your command..."
              value={command}
              onChange={(e) => setCommand(e.target.value)}
              rows="10"
            ></textarea>

            <button
              type="submit"
              className="bg-blue-500 text-white font-bold py-2 px-4 rounded-lg hover:bg-blue-600 transition"
            >
              Submit
            </button>
          </form>
        </div>
      </div>

      <footer className="absolute bottom-4 text-sm">
        <p>Powered by Paal AI</p>
      </footer>
    </div>
  );
};

export default App;
