import React, { useState } from 'react';

const App = () => {
  const [command, setCommand] = useState('');
  const [response, setResponse] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('http://localhost:5000/process-command', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ command }),
      });

      const data = await res.json();
      setResponse(data.message || 'Command executed successfully.');
    } catch (error) {
      setResponse('Error: Unable to execute the command.');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-600 via-pink-500 to-purple-700 text-white flex flex-col items-center justify-start relative">
      {/* Header Section */}
      <header className="absolute" style={{ top: '2cm', left: '2cm', display: 'flex', alignItems: 'center', justifyContent: 'flex-start' }}>
        <img 
          src="/paal-logo.png" 
          alt="Paal AI Logo" 
          style={{ height: '50px', width: 'auto', marginRight: '12px' }}
        />
        <div style={{ color: 'white' }}>
          <h1 className="text-3xl font-bold" style={{ margin: 0 }}>Paal AI</h1>
        </div>
      </header>

      {/* Center Content */}
      <div className="absolute" style={{ top: '12cm', textAlign: 'center' }}>
        <h2 className="text-4xl font-bold mb-6">AI-Powered-PC</h2>
        <form onSubmit={handleSubmit} className="flex flex-col items-center space-y-4">
          <textarea
            className="border border-gray-300 rounded-lg p-4 resize-none focus:outline-none focus:ring-2 focus:ring-blue-500 text-black"
            placeholder="Enter your command..."
            value={command}
            onChange={(e) => setCommand(e.target.value)}
            rows="5"
            style={{ width: '500px' }}
          ></textarea>
          <button
            type="submit"
            className="bg-blue-500 text-white font-bold py-2 px-8 rounded-lg hover:bg-blue-600 transition"
          >
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default App;
